mapper.select:
select question
from neuedu_user
where username = #{username}

select count(id)
from neuedu_user
where username = #{username} and question = #{question} and answer = #{answer}

mapper.update:
update neuedu_user
password = #{user.password}, email = #{user.email}, phone = #{user.phone}, question = #{user.question}, answer = #{user.answer}, update_time = now()
id=#{user.id}
